import { Spin } from 'antd';
import { Fragment } from 'react';

const runAni = () => this.document.querySelector("#__next").style.overflow = "hidden"
const removeAni = () => this.document.querySelector("#__next").style.overflow = "auto"

const LoadingPage = ({isLoading = false,children}) => {

    return isLoading ? (
        <div className="overlay">
            <div className="blur"></div>
            <Spin size="large" className="iconSpinner"/>
            {children}
            {runAni()}
        </div>
    ) : (
        <Fragment>
            {children}
            {removeAni()}
        </Fragment>

    )
}

export default LoadingPage;
