import { withRouter } from "next/router";
import { Component, Fragment } from "react";
import Layout1 from "../../layouts/layout1";
import MyRouter from "../../const/routes";
import { Breadcrumb, Row, Col } from "antd";
import Link from "next/link";
import MyService from "../../services/myService";

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faChevronRight } from '@fortawesome/free-solid-svg-icons'

// data
import { dataCategory, dataProductPosition } from "../../data/data";

// import "jquery";
import "antd/dist/antd.css";
import "../../scss/pages/productsPosition.scss";

export async function getStaticPaths() {
  const paths = dataCategory.map(data => `/products/${data.slug}`);
  return {
    paths,
    fallback: false
  };
}

export async function getStaticProps({ params }) {
  await run();
  return { props: { name: "aaa" } };
}

class ProductDetail extends Component {
  listBreadcrum = [
    {
      name: "All Products",
      href: MyRouter.Products.href,
      as: ""
    },
    {
      name: MyService.parseSeoToName(
        this.props.router.query[Object.keys(this.props.router.query)[0]]
      ),
      href: this.props.router.pathname,
      as: this.props.router.asPath
    }
  ];

  renderBreadcrumb = () => {
    return this.listBreadcrum.map((item, i) => {
      return (
        <Breadcrumb.Item key={i}>
          <Link href={item.href} as={item.as}>
            <span className="mylink fs-15">{item.name}</span>
          </Link>
        </Breadcrumb.Item>
      );
    });
  };

  renderItem = (res) => {
    return res.data.map((res2, i2) => {
      return i2 < 3 ? (
        <Col span={6} style={{ marginTop: '12px' }} key={i2}>
          <div className="itemImage" key={i2}>
            <Link href="/">
              <img className="imgItem" src={`/images/${res2.url}`} style={{cursor:'pointer'}}/>
            </Link>
            <span className="title" style={{ width: '120px', display: 'block' }}>{res2.name}</span>
          </div>
        </Col>
      ) : null
    })
  }

  renderRemainItem = (res) => {
    return res.data.length === 3 ?
    (
      <Link href="/">
        <p className="mylink mt-4 mb-0 fs-16 font-bold">Show All {res.name} <FontAwesomeIcon icon={faChevronRight} /> </p>
      </Link>
    ) :
    res.data.map((res3, i3) => {
      if(i3 > 2){
        return i3 + 2 > res.data.length ?
        (
          <Fragment>
            <Link href="/">
              <p key={i3} className="mylink mt-8 mb-0 fs-16">{res3.name}</p>
            </Link>
            <Link href="/">
              <p className="mylink mt-8 mb-0 fs-16 font-bold">Show All {res.name} <FontAwesomeIcon icon={faChevronRight} /> </p>
            </Link>
          </Fragment>
        ) : (
          <Link href="/">
            <p  key={i3} className="mylink mt-8 mb-0 fs-16">{res3.name}</p>
          </Link>
        )
      }
      return null
    })
  }


  render() {
    return (
      <Layout1>
        <div className="container" id="productPosition">
          <Breadcrumb>{this.renderBreadcrumb()}</Breadcrumb>
          <h1>
            {MyService.parseSeoToName(this.props.router.query[Object.keys(this.props.router.query)[0]])}
          </h1>
          {
            dataProductPosition.map((res, i) => {
              return (
                <div className="mb-40">
                  <h2 className="fs-19 mt-25">{res.name}</h2>
                  <Row>
                    {
                      this.renderItem(res)
                    }
                    <Col span={6} style={{ marginTop: '12px' }}>
                      {
                       this.renderRemainItem(res)
                      }
                    </Col>
                  </Row>
                </div>
              )
            })
          }
        </div>
      </Layout1>
    );
  }
}

const run = () => {
  return new Promise((resolve, reject) => {
    setTimeout(() => {
      resolve("a");
    }, 500);
  });
};

export default withRouter(ProductDetail);
