        Comp : null
    }

    componentDidUpdate(prevProps, prevState) {
        if (!this.state.Comp) {
          this.lazyLoadTodo();
        }
    }

    lazyLoadTodo = async () => {
        let Intro = await import('../../components/IndexPage/Introduce');
        let ok = NewHOC(Intro);
        this.setState({Comp: Intro});
    }

    render(){

        const {Comp} = this.state;
