import { withRouter } from "next/router";
import { Component, Fragment } from "react";
import Layout1 from "../../layouts/layout1";
import MyRouter from "../../const/routes";
import { Breadcrumb, Row, Col } from "antd";
import Link from "next/link";
import MyService from "../../services/myService";

// data
import { dataCategory, dataProductPosition } from "../../data/data";

import "jquery";
import "../../scss/index.scss";
import "antd/dist/antd.css";





export async function getStaticPaths() {
  const paths = dataCategory.map(data => `/products/${data.slug}`);
  return {
    paths,
    fallback: false
  };
}

export async function getStaticProps({ params }) {
  await run();
  return { props: { name: 'aaa' } };
}

class ProductDetail extends Component {

  listBreadcrum = [
    {
      name: "All Products",
      href: MyRouter.Products.href,
      as: ''
    },
    {
      name: MyService.parseSeoToName(this.props.router.query[Object.keys(this.props.router.query)[0]]),
      href: this.props.router.pathname,
      as: this.props.router.asPath
    }
  ]

  renderBreadcrumb = () => {
    return this.listBreadcrum.map((item, i) => {
      return (
        <Breadcrumb.Item key={i}>
          <Link href={item.href} as={item.as}><span className="mylink fs-15">{item.name}</span></Link>
        </Breadcrumb.Item>
      )
    })
  }

  renderItem = (item) => {
    console.log(item)
    return item.map((res, i) => {
      console.log(i);
      return i <= 3 ?
        <Col span={6} style={{ marginTop: '0' }}>
          <img src={`/images/${res.url}`}></img>
        </Col> : null
    })
  }
  renderData = () => {
    return dataProductPosition.map((item, i) => {
      return (
        <Fragment key={i}>
          <h2 className="fs-18">{item.name}</h2>
        </Fragment>
      )
    })
  }

  render() {
    return (
      <Layout1>
        <div className="container">
          <Breadcrumb>
            {this.renderBreadcrumb()}
          </Breadcrumb>
          <h1>{MyService.parseSeoToName(this.props.router.query[Object.keys(this.props.router.query)[0]])}</h1>
          {this.renderData()}
        </div>
      </Layout1>
    )
  }
}

const run = () => {
  return new Promise((resolve, reject) => {
    setTimeout(() => {
      resolve("a")
    }, 500);
  })
}

export default withRouter(ProductDetail);
