import { withRouter } from "next/router";
import { dataCategory } from "../../data/data";
import { faRunning } from "@fortawesome/free-solid-svg-icons";
import { Component } from "react";
import Layout1 from "../../layouts/layout1";

import "jquery";
import "../../scss/index.scss";
import "antd/dist/antd.css";
import MyRouter from "../../const/routes";
import { Breadcrumb } from "antd";
import Link from "next/link";

export async function getStaticPaths() {
  const paths = dataCategory.map(data => `/products/${data.slug}`);
  return {
    paths,
    fallback: false
  };
}

export async function getStaticProps({ params }) {
  await run();
  return { props: { name: 'aaa' } };
}

class ProductDetail extends Component {

  listBreadcrum = [
    {
      name: 'All Products',
      href: MyRouter.Products.href,
      as: ''
    },
    {
      name: this.props.router.query.name,
      href: this.props.router.pathname,
      as: this.props.router.asPath
    }
  ]

  renderBreadcrumb = () => {
    return listBreadcrum.map((item,i) => {
      return (
        <Breadcrumb>
          <Breadcrumb.Item>
            <Link href={item.href} as={item.as}><span className="mylink">{item.name}</span></Link>
          </Breadcrumb.Item>
        </Breadcrumb>
      )
    })
  }

  render() {
    return (
      <Layout1>
        {this.renderBreadcrumb()}
        <div>ok</div>
      </Layout1>
    )
  }
}

const run = () => {
  return new Promise((resolve, reject) => {
    setTimeout(() => {
      resolve("a")
    }, 1000);
  })
}

export default withRouter(ProductDetail);
