import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPhotoVideo, faShoppingBasket, faUserAstronaut, faBookReader, faGift } from '@fortawesome/free-solid-svg-icons'
import Container from '@material-ui/core/Container';
import Link from "next/link";

import React, { Component } from 'react';


const data = {
    1: [
        {
            title:'kitchen & dining',
            data: ['kitchen','dining room','pantry','great room']
        },
        {
            title:'bed & bath',
            data: ['bath room','powder room','bedroom','baby & kids']
        },
        {
            title:'living',
            data: ['living room','family room','sunroom','home theater']
        },
        {
            title:'outdoor',
            data: ['landscape','Patio','Deck','Pool']
        },
        {
            title:'more room',
            data: ['exterior','entry','hall','home office']
        },

    ],
}

const appendMenu = (stt,datas) => {
    let resData = ""
    for(let data of datas){
        let sumList = ""
        for(let list of data.data){
            sumList+=`<li><small>${list}</small></li>`
        }
        resData+= `
            <div class="itemMenu">
                <h4 class="title">${data.title}</h6>
                <ul style="margin-bottom:0;list-style-type: none">
                    ${<Link href="/aa">aaa</Link>}
                </ul>
            </div>
        `
    }
    if(stt >= 1 && stt <= 3){
        $(`#MegaMenu .parent .item${stt}`).append(
            `
                <div class="menu" style="left:${
                    stt === 1 ? '-1px' : 
                    stt === 2 ? '-140px' :
                    stt === 3 ? '-400px' : ''
                }">
                    <div style="display:flex">
                        ${resData}
                    </div>

                </div>
            `
        )
    }
    if(stt > 3 && stt <= 5){
        $(`#MegaMenu .parent .item${stt}`).append(
            `
                <div class="menu" style="right:${
                    stt === 5 ? '-1px' : '-120px'
                }">
                    <div style="display:flex">
                        ${resData}
                    </div>
                </div>
            `
        )
    }
}

const alignDivMenu = (div,stt) => {
    $(div).find(".menu").css({'display':'block'})
    if(stt >= 1 && stt <= 3){
        let px;
        px = stt === 1 ? '-1px' : stt === 2 ? '-140px' : stt === 3 ? '-400px' : '0'
        $(div).find(".menu").css({
            left: px
        })
    }
    if(stt > 3 && stt <= 5){
        let px;
        px = stt === 5 ? '-1px' : '-120px'
        $(div).find(".menu").css({
            left: px
        })
    }
}


const HoverMenu = () => {
    $("#MegaMenu .item").hover(function () {
        $(this).addClass("hoverMenu")
        // get stt
        let stt = $(this).attr("class").split(" ")[1];
        stt = parseInt(stt[stt.length - 1]);
        // line transparent
        $(this).append(`
            <div class="line"></div>
        `)
        //css
        alignDivMenu(this,stt)
    }, function() {
        $("#MegaMenu .line").remove();
        $(this).removeClass("hoverMenu");
        $(this).find(".menu").css({'display':'none'})
    })
}

export default class MegaMenu extends Component {

    componentDidMount(){
        HoverMenu();
    }

    render() {
        return (
            <Container id="MegaMenu">
                <div className="parent">
                    <div className="item item1">
                        <FontAwesomeIcon icon={faPhotoVideo} />
                        <span>photos</span>
                        <div className="menu">
                            <div style={{display:'flex'}}>
                                <div className="itemMenu" s>
                                    <h4 className="title">aaa</h4>
                                    <ul style={{marginBottom:0,listStyleType:'none'}}>
                                        <small><Link href="/aa">aaa</Link></small>
                                    </ul>
                                </div>
                                <div className="itemMenu">
                                    <h4 className="title">bbb</h4>
                                    <ul style={{marginBottom:0,listStyleType:'none'}}>
                                    <small><Link href="/bb">bbb</Link></small>
                                    </ul>
                                </div>
                            </div>

                        </div>
                    </div>
                    <div className="item item2">
                        <FontAwesomeIcon icon={faShoppingBasket} />
                        <span>shop by department</span>
                        <div className="menu"></div>
                    </div>
                    <div className="item item3">
                        <FontAwesomeIcon icon={faUserAstronaut} />
                        <span>shop by department</span>
                        <div className="menu"></div>
                    </div>
                    <div className="item item4">
                        <FontAwesomeIcon icon={faBookReader} />
                        <span>stories & advice</span>
                        <div className="menu"></div>
                    </div>
                    <div className="item item5">
                        <FontAwesomeIcon icon={faGift} />
                        <span>SALE</span>
                        <div className="menu"></div>
                    </div>
                </div>
            </Container>
        )
    }
}
