import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPhotoVideo, faShoppingBasket, faUserAstronaut, faBookReader, faGift } from '@fortawesome/free-solid-svg-icons'


import React, { Component } from 'react'

const appendMenu = (stt,data) => {
    if(stt >= 1 && stt <= 3){
        $("#MegaMenu .parent").append(
            `
                <div class="menu" style="left:${
                    stt === 1 ? '0' : 
                    stt === 2 ? '80px' :
                    stt === 3 ? '30%' : '0'
                }">
                    aaaa
                </div>
            `
        )
    }
    if(stt > 3 && stt <= 5){
        $("#MegaMenu .parent").append(
            `
                <div class="menu" style="right:${
                    stt === 5 ? '0' : '80px'
                }">
                    aaaa
                </div>
            `
        )
    }
}

const HoverMenu = () => {
    // hover
    $("#MegaMenu .item").hover(function () {
        $(this).addClass("hoverMenu")
        // get stt
        let stt = $(this).attr("class").split(" ")[1];
        stt = parseInt(stt[stt.length - 1]);
        // append megaMenu
        appendMenu(stt,data);

    // remove hover
    }, function() {
        $(this).removeClass("hoverMenu");
        $("#MegaMenu .menu").remove();
    })
}

export default class MegaMenu extends Component {

    componentDidMount(){
        HoverMenu();
    }

    render() {
        return (
            <div id="MegaMenu" className="container">
                <div className="parent">
                    <div className="item item1">
                        <FontAwesomeIcon icon={faPhotoVideo} />
                        <span>photos</span>
                    </div>
                    <div className="item item2">
                        <FontAwesomeIcon icon={faShoppingBasket} />
                        <span>shop by department</span>
                    </div>
                    <div className="item item3">
                        <FontAwesomeIcon icon={faUserAstronaut} />
                        <span>shop by department</span>
                    </div>
                    <div className="item item4">
                        <FontAwesomeIcon icon={faBookReader} />
                        <span>stories & advice</span>
                    </div>
                    <div className="item item5">
                        <FontAwesomeIcon icon={faGift} />
                        <span>SALE</span>
                    </div>
                </div>
            </div>
        )
    }
}
