import { Row, Col } from "antd";
import Link from "next/link";
import { Fragment } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faChevronRight } from "@fortawesome/free-solid-svg-icons";


const CateComp0 = ({productsChild,productsChild_2}) => {
    return productsChild.map((res, i) => {
        const productsChild_2_filter = productsChild_2.filter(val => {
            let kq = val.parent.filter(x => x === res.id)
            return kq.length > 0 ? true : false
        })

        console.log(productsChild_2_filter)

        return (
            <div className="mb-40" key={i}>
                <h2 className="fs-19 mt-25">{res.name}</h2>
                <Row>
                    {/* {renderItems_productsChild(productsChild_2_filter)} */}
                    <Col span={6} style={{ marginTop: "12px" }}>
                        {/* {renderRemainItems_productsChild(productsChild_2_filter)} */}
                    </Col>
                </Row>
            </div>
        );
    });
};
const renderItems_productsChild = (productsChild_2) => {
    return productsChild_2.map((res2, i2) => {
        return i2 < 3 ? (
            <Col span={6} style={{ marginTop: '12px' }} key={i2}>
                <div className="itemImage">
                    <Link href="/">
                        <img className="imgItem" src={`/images/${res2.url}`} style={{ cursor: 'pointer' }} />
                    </Link>
                    <span className="title" style={{ width: '120px', display: 'block' }}>{res2.name}</span>
                </div>
            </Col>
        ) : null
    })
}
const renderRemainItems_productsChild = (items) => {
    return items.length === 3 ?
        (
            <Link href="/">
                <p className="mylink mt-4 mb-0 fs-16 font-bold">Show All {items.name} <FontAwesomeIcon icon={faChevronRight} /> </p>
            </Link>
        ) :
        items.children.map((res3, i3) => {
            if (i3 > 2) {
                return i3 + 2 > items.length ?
                    (
                        <Fragment key={i3}>
                            <Link href="/">
                                <p className="mylink mt-8 mb-0 fs-16">{res3.name}</p>
                            </Link>
                            <Link href="/">
                                <p className="mylink mt-8 mb-0 fs-16 font-bold">Show All {res.name} <FontAwesomeIcon icon={faChevronRight} /> </p>
                            </Link>
                        </Fragment>
                    ) : (
                        <Link href="/" key={i3}>
                            <p className="mylink mt-8 mb-0 fs-16">{res3.name}</p>
                        </Link>
                    )
            }
            return null
        })
}



const CategoryComp = ({ product, productsChild, productsChild_2}) => {
    const choose = {
        0: <CateComp0 productsChild={productsChild} productsChild_2={productsChild_2}/>,
        1: <div>2</div>,
        2: <div>3</div>
    };
    return choose[product.typeShow];
};

export default CategoryComp;
