import React, { Component } from 'react'

export default class ItemDetail extends Component {

    static async getInitialProps({ query }) {
        const { itemId } = query;
        const { data } = await axios.get(`http://localhost:3000/item/${itemId}`);
        return {
            item: data
        }
    }

    render() {
        return (
            <div>
                {this.props.item}
            </div>
        )
    }
}
