import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPhotoVideo, faShoppingBasket, faUserAstronaut, faBookReader, faGift } from '@fortawesome/free-solid-svg-icons'
import ReactMegaMenu from "react-mega-menu"


import React, { Component } from 'react'

const appendMenu = (stt,data) => {
    if(stt >= 1 && stt <= 3){
        $("#MegaMenu .parent").append(
            `
                <div class="menu" style="left:${
                    stt === 1 ? '0' : 
                    stt === 2 ? '80px' :
                    stt === 3 ? '30%' : '0'
                }">
                    aaaa
                </div>
            `
        )
    }
    if(stt > 3 && stt <= 5){
        $("#MegaMenu .parent").append(
            `
                <div class="menu" style="right:${
                    stt === 5 ? '0' : '80px'
                }">
                    aaaa
                </div>
            `
        )
    }
}
const HoverMenu = () => {
    // hover
    $("#MegaMenu .item").hover(function () {
        $(this).addClass("hoverMenu")
        // get stt
        let stt = $(this).attr("class").split(" ")[1];
        stt = parseInt(stt[stt.length - 1]);
        // append megaMenu
        appendMenu(stt);

    // remove hover
    }, function() {
        $(this).removeClass("hoverMenu");
        $("#MegaMenu .menu").remove();
    })
}

export default class MegaMenu extends Component {

    componentDidMount(){
        HoverMenu();
    }

