import Layout1 from "../../layouts/layout1";
import { Component } from "react";
import { Row, Col } from "antd";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faChevronRight} from '@fortawesome/free-solid-svg-icons'

import 'jquery';
import "../../scss/index.scss";
import "antd/dist/antd.css";


const data = [
    {
        id: 1,
        name: 'Furniture',
        url:'cate1.jpg',
        data: [
            {
                id: 1,
                name: 'Living Room Furniture'
            },
            {
                id: 2,
                name: 'Kitchen & Dining Furniture'
            },
            {
                id: 3,
                name: 'Bedroom Furniture'
            },
            {
                id: 4,
                name: 'Bedroom Furniture'
            },
            {
                id: 5,
                name: 'Home Office Furniture'
            }]
    },
    {
        id: 2,
        name: 'Kitchen & Dining',
        url:'cate2.jpg',
        data: [
            {
                id: 1,
                name: 'Kitchen & Dining Furniture'
            },
            {
                id: 2,
                name: 'Kitchen Fixtures'
            },
            {
                id: 3,
                name: 'Tile'
            },
            {
                id: 4,
                name: 'Kitchen Storage & Organization'
            },
            {
                id: 5,
                name: 'Tabletop'
            }
        ]
    },
    {
        id: 3,
        name: 'Bath',
        url:'cate3.jpg',
        data: [
            {
                id: 1,
                name: 'Bathroom Vanities'
            },
            {
                id: 2,
                name: 'Bathroom Vanity Lighting'
            },
            {
                id: 3,
                name: 'Tile'
            },
            {
                id: 4,
                name: 'Showers'
            },
            {
                id: 5,
                name: 'Bathtubs'
            }
        ]
    }
]

class ProductPage extends Component {

    renderProducts = () => {
        return data.map((res,i) => {
            return (
                <Col span={8} key={i}>
                    <div className="product">
                        <img src={`/images/${res.url}`}/>
                        <span className="title">{res.name}</span>
                        <div className="list mt-20">
                            {   
                                res.data.map((res2,i2) => {
                                    return (i2 + 1 < res.data.length) ?  
                                    <p key={i2} className="mylink mb-8">{res2.name}</p> :
                                    <div>
                                        <p key={i2} className="mylink mb-8">{res2.name}</p>
                                        <p key={i2} className="mylink mb-8" style={{fontWeight:'bold'}}>Show All {res.name} <FontAwesomeIcon/></p>
                                    </div>
                            })}
                        </div>
                    </div>
                </Col>
            )
        })
    }

    render() {
        const products = this.renderProducts();

        return (
            <Layout1>
                <div id="indexProduct">
                    <div className="container">
                        <Row>
                            {products}
                        </Row>
                    </div>
                </div>
            </Layout1>
        )
    }

}

export default ProductPage;
