import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPhotoVideo, faShoppingBasket, faUserAstronaut, faBookReader, faGift } from '@fortawesome/free-solid-svg-icons'

import React, { Component } from 'react';


const data = {
    1: [
        {
            title:'kitchen & dining',
            data: ['kitchen','dining room','pantry','great room']
        },
        {
            title:'bed & bath',
            data: ['bath room','powder room','bedroom','baby & kids']
        },
        {
            title:'living',
            data: ['living room','family room','sunroom','home theater']
        },
        {
            title:'outdoor',
            data: ['landscape','Patio','Deck','Pool']
        },
        {
            title:'more room',
            data: ['exterior','entry','hall','home office']
        },

    ],
}

const appendMenu = (stt,datas) => {
    let resData = ""
    for(let data of datas){
        let sumList = ""
        for(let list of data.data){
            sumList+=`<li><small>${list}</small></li>`
        }
        resData+= `
            <div class="itemMenu">
                <h6>${data.title}</h6>
                <ul class="list-unstyled">
                    ${sumList}
                </ul>
            </div>
        `
    }


    if(stt >= 1 && stt <= 3){
        $(`#MegaMenu .parent .item${stt}`).append(
            `
                <div class="menu" style="left:${
                    stt === 1 ? '-1px' : 
                    stt === 2 ? '-80px' :
                    stt === 3 ? '-200px' : ''
                }">
                    <div class="d-flex">
                        ${resData}
                    </div>

                </div>
            `
        )
    }
    if(stt > 3 && stt <= 5){
        $(`#MegaMenu .parent .item${stt}`).append(
            `
                <div class="menu" style="right:${
                    stt === 5 ? '0' : '80px'
                }">
                    aaaa
                </div>
            `
        )
    }
}


const HoverMenu = () => {
    $("#MegaMenu .item").hover(function () {
        $(".item").not($(this)).addClass("zIndex")
        $(this).addClass("hoverMenu")
        // get stt
        let stt = $(this).attr("class").split(" ")[1];
        stt = parseInt(stt[stt.length - 1]);
        // append megaMenu
        appendMenu(stt,data[1]);
    }, function() {
        $(".item").not(this).removeClass("zIndex")
        $(this).removeClass("hoverMenu");
        $("#MegaMenu .menu").remove();
    })
}

export default class MegaMenu extends Component {

    componentDidMount(){
        HoverMenu();
    }

    render() {
        return (
            <div id="MegaMenu" className="container">
                <div className="parent">
                    <div className="item item1">
                        <FontAwesomeIcon icon={faPhotoVideo} />
                        <span>photos</span>
                    </div>
                    <div className="item item2">
                        <FontAwesomeIcon icon={faShoppingBasket} />
                        <span>shop by department</span>
                    </div>
                    <div className="item item3">
                        <FontAwesomeIcon icon={faUserAstronaut} />
                        <span>shop by department</span>
                    </div>
                    <div className="item item4">
                        <FontAwesomeIcon icon={faBookReader} />
                        <span>stories & advice</span>
                    </div>
                    <div className="item item5">
                        <FontAwesomeIcon icon={faGift} />
                        <span>SALE</span>
                    </div>
                </div>
            </div>
        )
    }
}
