import { Row, Col } from "antd";
import Link from "next/link";
import { Fragment } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faChevronRight } from "@fortawesome/free-solid-svg-icons";

const SoLuongHienThi = 4;
const CateComp0 = ({ productsChild, productsChild_2 }) => {
    return productsChild.map((res, i) => {
        const productsChild_2_filter = productsChild_2.filter(val => {
            let kq = val.parent.filter(x => x === res.id)
            return kq.length > 0 ? true : false
        })
        return (
            <div className="mb-40" key={i}>
                <h2 className="fs-19 mt-25">{res.name}</h2>
                <Row>
                    {renderItems_productsChild(productsChild_2_filter)}
                    <Col span={6} style={{ marginTop: "12px" }}>
                        {renderRemainItems_productsChild(productsChild_2_filter)}
                    </Col>
                </Row>
            </div>
        );
    });
};
const renderItems_productsChild = (productsChild_2_filter) => {
    if (productsChild_2_filter.length > 0) {
        return productsChild_2_filter.map((res2, i2) => {
            return i2 < 3 ? (
                <Col span={6} style={{ marginTop: '12px' }} key={i2}>
                    <div className="itemImage">
                        <Link href="/">
                            <img className="imgItem" src={`/images/${res2.url}`} style={{ cursor: 'pointer' }} />
                        </Link>
                        <span className="title" style={{ width: '120px', display: 'block' }}>{res2.name}</span>
                    </div>
                </Col>
            ) : null
        })
    }
    return null
}
const renderRemainItems_productsChild = (productsChild_2_filter) => {
    if (productsChild_2_filter.length === SoLuongHienThi) {
        return (
            <Col span={6} style={{ marginTop: '12px' }}>
                <div className="itemImage">
                    <Link href="/">
                        <img className="imgItem" src={`/images/${productsChild_2_filter[SoLuongHienThi - 1].url}`} style={{ cursor: 'pointer' }} />
                    </Link>
                    <span className="title" style={{ width: '120px', display: 'block' }}>{productsChild_2_filter[SoLuongHienThi - 1].name}</span>
                </div>
            </Col>
        )
    }
    if (productsChild_2_filter.length > SoLuongHienThi) {
        return productsChild_2_filter.map((res,i) => {
            return i >= SoLuongHienThi ? 
            (
                <Col span={6} style={{ marginTop: '12px' }} key={i}>
                    <Link href="/">
                        <p className="mylink mt-8 mb-0 fs-16">{res3.name}</p>
                    </Link>
                </Col>
            ) : null
        })
    }
    return null
}


