import Layout1 from "../../layouts/layout1";
import { Component } from "react";
import { Row, Col, Carousel } from "antd";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faChevronRight } from "@fortawesome/free-solid-svg-icons";

import "jquery";
import "../../scss/index.scss";
import "antd/dist/antd.css";
import { dataCategory } from "../../data/data";
import Link from "next/link";
import { withRouter } from "next/router";

class ProductPage extends Component {
  renderProducts = () => {
    return dataCategory.map((res, i) => {
      return (
        <Col span={8} key={i}>
          <div className="product">
            <Link
              href={{ pathname: "/products/[name]", query: { id: res.id } }}
              as={`/products/${res.name}`}
            >
              <img src={`/images/${res.url}`} />
            </Link>
            <Link
              href={{ pathname: "/products/[name]", query: { id: res.id } }}
              as={`/products/${res.name}`}
            >
              <span className="title mylink">{res.name}</span>
            </Link>
            <div className="list mt-10">
              {res.data.map((res2, i2) => {
                return i2 + 1 < res.data.length ? (
                  <p key={i2} className="mylink mb-5">
                    {res2.name}
                  </p>
                ) : (
                  <div key={i2}>
                    <p className="mylink mb-5">{res2.name}</p>
                    <p className="mylink mb-5" style={{ fontWeight: "bold" }}>
                      Show All {res.name}{" "}
                      <FontAwesomeIcon className="ml-5" icon={faChevronRight} />
                    </p>
                  </div>
                );
              })}
            </div>
          </div>
        </Col>
      );
    });
  };
  renderCategories = () => {
    return dataCategory.map((res, i) => {
      return (
        <Link
          key={i}
          href={{ pathname: "/products/[name]", query: { id: res.id } }}
          as={`/products/${res.id}`}
        >
          <p className="mylink mb-5">{res.name}</p>
        </Link>
      );
    });
  };

  render() {
    console.log("render: ",this.props);
    console.log("router: ",this.props)
    const products = this.renderProducts();
    const categories = this.renderCategories();

    return (
      <Layout1>
        <div id="indexProduct">
          <div className="container">
            <Row>
              <Col span={6} style={{ marginTop: "20px" }}>
                <div className="categories">
                  <h3 className="text-bold mb-20">Categories</h3>
                  {categories}
                </div>
              </Col>
              <Col span={18} style={{ marginTop: 0 }}>
                <Carousel autoplay>
                  <div className="img">
                    <img src="/images/banner1.jpg" alt="house1" />
                  </div>
                  <div className="img">
                    <img src="/images/banner2.jpg" alt="house1" />
                  </div>
                </Carousel>
              </Col>
              {products}
            </Row>
          </div>
        </div>
      </Layout1>
    );
  }
}


// This function gets called at build time
export async function getStaticPaths() {
    return {
      // Only `/posts/1` and `/posts/2` are generated at build time
      paths: [{ params: { id: 'cc' } }, { params: { id: 'cl' } }],
      // Enable statically generating additional pages
      // For example: `/posts/3`
      fallback: true
    }
  }

  // This also gets called at build time
  export async function getStaticProps({ params }) {
    // params contains the post `id`.
    // If the route is like /posts/1, then params.id is 1
    // const res = await fetch(`https://.../posts/${params.id}`)
    // const post = await res.json()
    console.log("huhu: ",params)
    // Pass post data to the page via props
    return { props: { post:'pót' } }
  }

export default withRouter(ProductPage);
